//Assignment 1.13 (Algebra: Solve 2 X 2 linear equations) 

public class Chapter1_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double x;
		double y;
		
		x = (44.5 * .55 - 50.2 * 5.9) / (3.4 * .55 - 50.2 * 2.1);
		y = (3.4 * 5.9 - 44.5 * 2.1) /  (3.4 * .55 - 50.2 * 2.1);
		
		System.out.println("x = " + (float)x);
		System.out.println("y = " + (float)y);
		

	}

}
